import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

  const [preferences, setPreferences] = useState({
    beds: '',
    price: '',
    livings: '',
    wc: '',
    area: '',
    ketchen: '',
    furnished: '',
    city: '',
    district: '',
    width: '',
    length: ''
  })

  const [recommendations, setRecommendations] = useState([]);
  const [recommendation, setRecommendation] = useState([]);

  const handleChange = (e) => {
    const {name, value} = e.target;
    setPreferences({ ...preferences, [name]: value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setRecommendations([]);

    fetch('http://localhost:3001/submit-prefs', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(preferences),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
      setRecommendations(data); // Update the state with the recs
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  useEffect(() => {
    fetch('http://localhost:3001/get-recommendations')
        .then(response => response.json())
        .then(data1 => {
            setRecommendation(data1);
        })
        .catch(error => console.error('Error fetching recommendations:', error));
  }, []); // The empty array ensures this effect runs only once after the initial render


  return (
    <div className='App'>
      <h2>Property Prefrences</h2>
      <form onSubmit={handleSubmit}>
        {/*Dynamically generate input fields based on the prefrences state */}
        {Object.keys(preferences).map((key) => (
          <div key={key}>
            <label>
              {key.charAt(0).toUpperCase() + key.slice(1)};
              <input 
                type="text"
                name={key}
                value={preferences[key]}
                onChange={handleChange}
              />
            </label>
          </div>
        ))}
        <button type='submit'>Submit</button>
      </form>

      <div>
        <h2>Search Results</h2>
        <ul className='recommendations'>
          {recommendations.map((rec, index) => (
            <li key={index}>
              ID: {rec.id}, Price: {rec.price}, Bedrooms: {rec.beds}, Bathrooms: {rec.wc}, Area: {rec.area}, Kitchens: {rec.ketchen}, Furnished: {rec.furnished}, City: {rec.city}, District: {rec.district}, Width: {rec.width}, Length: {rec.length}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h1>Recommended Properties</h1>
        <ul>
            {recommendation.map(rec => (
                <li key={rec.id}>
                    ID: {rec.id}, Price: {rec.price}, Bedrooms: {rec.beds}, Bathrooms: {rec.wc}, Area: {rec.area}, Kitchens: {rec.ketchen}, Furnished: {rec.furnished}, City: {rec.city}, District: {rec.district}, Width: {rec.width}, Length: {rec.length}, Rating: {rec.userreview}
                </li>
              ))}
          </ul>
      </div>
    </div>
  );
}

export default App;


