import React, { useState } from 'react';
import './App.css';

function App() {

  const [preferences, setPreferences] = useState({
    beds: '',
    price: '',
    livings: '',
    wc: '',
    area: '',
    ketchen: '',
    furnished: '',
    city: '',
    district: '',
    width: '',
    length: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target;
    setPreferences({ ...preferences, [name]: value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/submit-prefs', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(preferences),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
      // Here you would typically update the state with the received recommendations and display them
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };


  return (
    <div className='App'>
      <h2>Property Prefrences</h2>
      <form onSubmit={{handleSubmit}}>
        {/*Dynamically generate input fields based on the prefrences state */}
        {Object.keys(preferences).map((key) => (
          <div key={key}>
            <label>
              {key.charAt(0).toUpperCase() + key.slice(1)};
              <input 
                type="text"
                name={key}
                value={preferences[key]}
                onChange={handleChange}
              />
            </label>
          </div>
        ))}
        <button type='submit'>Submit</button>
      </form>
    </div>
  );
}

export default App;
