import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './propertylisting.css';

const PropertyListing = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/api/properties/${id}`)
      .then(response => response.json())
      .then(data => {
        setProperty(data);
      })
      .catch(error => console.log(error));
  }, [id]);

  if (!property) return <div>Loading...</div>;

  const containerStyle = {
    width: '100%', // Adjusted for full width or specify a larger fixed width
    height: '500px' // Increased height for a larger map view
  };

  const center = {
    lat: property.location.lat,
    lng: property.location.lng
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div className="bg-white">
        {/* Other sections of your component */}

        {/* Google Map Section */}
        <LoadScript
          googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" // Replace with your API key
        >
          <GoogleMap
            mapContainerStyle={containerStyle}
            center={center}
            zoom={15}
          >
            <Marker
              position={center}
              icon={{
                url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png" // This is a common URL for a red pin
              }}
            />
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default PropertyListing;
